$arte_1_l2[0] = "<b><i>Fior di Battaglia</b></i>: MS Ludwig XV 13 - <a href=\"http://www.getty.edu/art/collection/objects/1443/unknown-fiore-furlan-dei-liberi-da-premariacco-il-fior-di-battaglia-italian-about-1410/\" target=\"_blank\" title=\"J. Paul Getty Museum (codice 'GETTY')\">J. Paul Getty Museum (codice \"GETTY\")</a>";
$arte_1_l3[0] = "<b><i>Flos Duellatorum (Pisani-Dossi MS)</b></i>: <a href=\"http://wiktenauer.com/wiki/Flos_Duellatorum_(Pisani_Dossi_MS)\" target=\"_blank\" title=\"Pisani-Dossi MS (codice 'Pisani-Dossi MS')\">F. Novati, Flos duellatorum</a>: Il Fior di battaglia di maestro Fiore dei Liberi da Premariacco (Bergamo, 1902)";
